
export class Context {
    #values: { [key: string]: string };
    spanCtx: any;

    constructor() {
        this.#values = {};
    }
    clone(): Context {
        const ctx = new Context()
        ctx.spanCtx = this.spanCtx;
        ctx.#values = { ...this.#values }
        return ctx
    }
    debug(message: string) {
        console.debug(`DEBUG: ${message}`);
    }
    info(message: string) {
        console.debug(message)
    }
    error(message: any) {
        console.debug(message)
    }
    setValue = (dataKey: string, dataValue: string) => {
        this.#values[dataKey] = dataValue;
    }
    getValue = (dataKey: string): (string | undefined) => {
        return this.#values[dataKey];
    }
}
