import Prometheus from "prom-client";
import express from 'express';

const counter = new Prometheus.Counter({
    name: 'count_of_requests_by_url',
    help: 'Total number of request',
    labelNames: ['method', 'path', 'status_code'],
});

const latency = new Prometheus.Summary({
    name: 'latency_by_url',
    help: 'Request duration, in seconds',
    percentiles: [0.01, 0.1, 0.5, 0.75, 0.9, 0.99],
    labelNames: ['method', 'path', 'status_code'],
});


const prometheusMiddleware = (req: express.Request, res: express.Response, next: any) => {
    const { method, path } = req;
    counter.inc({ method, path, status_code: res.statusCode }, 1);
    const endRequestTimer = latency.startTimer();
    res.on('finish', () => {
        endRequestTimer({ method, path, status_code: res.statusCode });
    });
    next();
}

export default prometheusMiddleware;
