
export const getLogger = () => {
    const format = (log: (str: string) => void) => (msg: string, ...args: any[]) => {
        if (args.length) {
            log(`[Jaeger] ${msg}, args: ${args.join(', ')}`);
        } else {
            log(`[Jaeger] ${msg}`);
        }
    };

    const logger = {
        error: format(console.error),
        warn: format(console.warn),
        info: format(console.error),
    };
    return logger;
};
