const parse = require('../parse');

describe('parse Luxembourg id card mrz', () => {
  it('LUX ID', () => {
    const data = [
      '745051974386<<<<13.06.2013<<13.06.2023',
    ];

    const result = parse(data);
    expect(result).toMatchObject({
      format: 'LUXEMBOURG_ID_CARD',
      valid: true,
    });
    expect(result.fields).toStrictEqual({
      documentNumber: '745051974386',
      issueDate: '13062013',
      expirationDate: '13062023',
    });
  });
});
