const parse = require('../parse');

describe('parse Salvador TD1', () => {
  describe('document number', () => {
    it('8-digit format', () => {
      const data = [
        'IDSLV88888888<<88<<<<<<<<<<<<<',
        '7777777M7777777SLV<<<<<<<<<<<4',
        'AA<AA<A<AAAAAAA<<AAAAA<AAAAAAA',
      ];

      const result = parse(data);
      const { documentNumber } = result?.fields;

      expect(documentNumber).toEqual('888888888');
    });
  });
});
