const { SLOVAK_DRIVING_LICENSE } = require('../formats');

const { getValidLines } = require('../checkLines');
const getResult = require('../getResult');
const slovakDrivingLicenseFields = require('./slovakDrivingLicenseFields');

const parseSlovakDriverLicense = (mrzLines) => {
  const lines = getValidLines(mrzLines, 1, 30, SLOVAK_DRIVING_LICENSE);
  return getResult(SLOVAK_DRIVING_LICENSE, lines, slovakDrivingLicenseFields);
};

module.exports = parseSlovakDriverLicense;
