const { DOMINICAN_TD1 } = require('../formats');

const { getValidLines } = require('../checkLines');
const getResult = require('../getResult');
const { fields } = require('./td1-dominican-fields');
const createFieldParser = require('../createFieldParser');

const parseDominicanTD1 = (mrzLines) => {
  const lines = getValidLines(mrzLines, 3, 30, DOMINICAN_TD1);
  return getResult(DOMINICAN_TD1, lines, fields.map(createFieldParser));
};

module.exports = parseDominicanTD1;
