const { ECUADOR_TD1 } = require('../formats');

const { getValidLines } = require('../checkLines');
const getResult = require('../getResult');
const { fields } = require('./td1-ecuador-fields');
const createFieldParser = require('../createFieldParser');

const parseEcuadorTD1 = (mrzLines) => {
  const lines = getValidLines(mrzLines, 3, 30, ECUADOR_TD1);
  return getResult(ECUADOR_TD1, lines, fields.map(createFieldParser));
};

module.exports = parseEcuadorTD1;
