const { KUWAIT_TD1 } = require('../formats');

const { getValidLines } = require('../checkLines');
const getResult = require('../getResult');
const { fields } = require('./td1-kuwait-fields');
const createFieldParser = require('../createFieldParser');

const parseKuwaitTD1 = (mrzLines) => {
  const lines = getValidLines(mrzLines, 3, 30, KUWAIT_TD1);
  return getResult(KUWAIT_TD1, lines, fields.map(createFieldParser));
};

module.exports = parseKuwaitTD1;
