const { fields } = require('./td1Fields');
const { personalNumberTemplate } = require('../fieldTemplates');
const parsePersonalNumber = require('../../parsers/parsePersonalNumber');

const personalNumberRules = {
  ...personalNumberTemplate,
  line: 0,
  start: 15,
  end: 29,
  parser: parsePersonalNumber,
};
const moldovaFields = fields.map((field) => (field.field === 'optional1' ? personalNumberRules : field));

module.exports = {
  fields: moldovaFields,
};
