const { MOLDOVA_TD1 } = require('../formats');

const { getValidLines } = require('../checkLines');
const getResult = require('../getResult');
const { fields } = require('./td1-moldova-fields');
const createFieldParser = require('../createFieldParser');

const parseMoldovaTD1 = (mrzLines) => {
  const lines = getValidLines(mrzLines, 3, 30, MOLDOVA_TD1);
  return getResult(MOLDOVA_TD1, lines, fields.map(createFieldParser));
};

module.exports = parseMoldovaTD1;
