const { PAKISTAN_DOMESTIC_TD1 } = require('../formats');

const { getValidLines } = require('../checkLines');
const getResult = require('../getResult');
const { fields } = require('./td1-pakistan-fields');
const createFieldParser = require('../createFieldParser');

const parsePakistanTD1 = (mrzLines) => {
  const lines = getValidLines(mrzLines, 3, 30, PAKISTAN_DOMESTIC_TD1);
  return getResult(PAKISTAN_DOMESTIC_TD1, lines, fields.map(createFieldParser));
};

module.exports = parsePakistanTD1;
