const { POLAND_TD1 } = require('../formats');

const { getValidLines } = require('../checkLines');
const getResult = require('../getResult');
const { fields } = require('./td1-poland-fields');
const createFieldParser = require('../createFieldParser');

const parsePolandTD1 = (mrzLines) => {
  const lines = getValidLines(mrzLines, 3, 30, POLAND_TD1);
  return getResult(POLAND_TD1, lines, fields.map(createFieldParser));
};

module.exports = parsePolandTD1;
