const { SALVADOR_TD1 } = require('../../formats');

const { getValidLines } = require('../../checkLines');
const getResult = require('../../getResult');
const { fields } = require('./td1-salvador-fields');
const createFieldParser = require('../../createFieldParser');

const parseSalvadorTD1 = (mrzLines) => {
  const lines = getValidLines(mrzLines, 3, 30, SALVADOR_TD1);
  return getResult(SALVADOR_TD1, lines, fields.map(createFieldParser));
};

module.exports = parseSalvadorTD1;
