const { TD1 } = require('../formats');

const { getValidLines } = require('../checkLines');
const getResult = require('../getResult');
const { fields } = require('./td1Fields');
const createFieldParser = require('../createFieldParser');

const parseTD1 = (mrzLines) => {
  const lines = getValidLines(mrzLines, 3, 30, TD1);
  return getResult(TD1, lines, fields.map(createFieldParser));
};

module.exports = parseTD1;
