/* eslint-disable no-param-reassign */

const { TD2 } = require('../formats');

const { getValidLines } = require('../checkLines');
const getResult = require('../getResult');
const TD2Fields = require('./td2Fields');

module.exports = function parseTD2(mrzLines) {
  const lines = getValidLines(mrzLines, 2, 36, TD2);

  return getResult(TD2, lines, TD2Fields);
};
